mapper.delete:
delete from TB_APPLY_ACTIVITY
where 
	ACTIVITY_ID = #{ACTIVITY_ID} and USER_ID=#{USER_ID}

mapper.select:
select a.ACTIVITY_ID, a.USER_ID, a.CREATEDATE, a.APPLY_ACTIVITY_ID, b.USERNAME, b.LINKNBR from TB_APPLY_ACTIVITY a , TB_BJJDUSERINFO b where a.USER_ID=b.BJJDUSERINFO_ID and a.ACTIVITY_ID= #{pd.ACTIVITY_ID}
and a.CREATEDATE between #{pd.lastLoginStart} and #{pd.lastLoginEnd}
and b.USERNAME LIKE CONCAT(CONCAT('%', #{pd.field1}),'%')
and b.LINKNBR LIKE CONCAT(CONCAT('%', #{pd.field3}),'%')
